mapper.select:
select bNo, bTitle, bDate, bContent, bReadCount, bCategory, mNo
from blog
where bNo=#{bNo}

mapper.update:
update blog
set bTitle=#{bTitle}, bContent=#{bContent}, bDate=sysdate
where bNo=#{bNo}

update blog_review 
set brDate=sysdate, brContent, brStep, brGroup, brDepth, mNo 
where brNo=#{brNo}
